import React, { useState, useEffect } from "react";
import ContactList from "./components/ContactList";
//import SearchBar from "./components/SearchBar";
import AddContactForm from "./components/AddContactForm";
import Loader from "./components/Loader";
import contactsData from "./data";
import './App.css';


function App() {
  const [contacts, setContacts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  

  // simulate API call
  useEffect(() => {
    setLoading(true);
    setError(null);

    // simulate 2 sec delay
    const timer = setTimeout(() => {
      // Randomly simulate failure (20% chance)
      if (Math.random() < 0.2) {
        setError("Failed to fetch contacts. Please try again.");
        setLoading(false);
      } else {
        setContacts(contactsData);
        setLoading(false);
      }
    }, 1500);

    return () => clearTimeout(timer);
  }, []);

  // Load contacts from localStorage
useEffect(() => {
  const saved = localStorage.getItem("contacts");
  if (saved) {
    setContacts(JSON.parse(saved));
  } else {
    setContacts(contactsData);
  }
  setLoading(false);
}, []);

// Save to localStorage whenever contacts change
useEffect(() => {
  localStorage.setItem("contacts", JSON.stringify(contacts));
}, [contacts]);

  // const handleAddContact = (newContact) => {
  //   setContacts([...contacts, { id: contacts.length + 1, ...newContact }]);
  // };

  // âœ… Add new contact
  const handleAddContact = (newContact) => {
    const updatedContacts = [
      ...contacts,
      { id: contacts.length + 1, ...newContact },
    ];
    setContacts(updatedContacts);
  };

  const filteredContacts = contacts.filter((contact) =>
    contact.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // const filteredContacts = contacts.filter(contact =>
  //   contact.name.toLowerCase().includes(searchTerm.toLowerCase())
  // );

  return (
    <div className="min-h-screen flex flex-col items-center p-6 bg-gray-100">
      <div className="w-full max-w-3xl bg-white rounded-2xl shadow-lg p-6">
        {/* <h1 className="text-3xl font-bold text-center text-blue-600 mb-6">
          ðŸ“ž Contact List
        </h1> */}

        {loading ? (
          <Loader />
        ) : error ? (
          <div className="text-center text-red-500 bg-red-50 border border-red-200 rounded-lg p-4">
            <p>{error}</p>
            <button
              onClick={() => window.location.reload()}
              className="mt-3 bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
            >
              Retry
            </button>
          </div>
        ) : (
          <>
            {/* <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} /> */}
            <ContactList 
              contacts={filteredContacts}
              // contacts={filteredContacts}
              onAddContact={handleAddContact} // âœ… pass correctly
             />
            <div class="app-container">
               <AddContactForm onAddContact={handleAddContact} />
            </div>
            {/* <AddContactForm onAddContact={handleAddContact} /> */}
          </>
        )}
      </div>
    </div>
  );

  
}

export default App;
