import React, { useState } from "react";
import { Phone, Mail, User } from "lucide-react";

const contactsData = [
  { name: "Alice Johnson", phone: "9876543210", email: "alice@gmail.com" },
  { name: "Bob Smith", phone: "9123456780", email: "bob@gmail.com" },
  { name: "Charlie Brown", phone: "9988776655", email: "charlie@gmail.com" },
  { name: "David Miller", phone: "9000012345", email: "david@gmail.com" },
];

export default function ContactList() {
  const [searchTerm, setSearchTerm] = useState("");

  const filteredContacts = contactsData.filter((c) =>
    c.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const colors = ["bg-pink-100", "bg-blue-100", "bg-green-100", "bg-yellow-100"];

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-6">
      {/* Header */}
      <h1 className="text-3xl font-bold mb-4 flex items-center gap-2 text-gray-800">
        <Phone className="text-red-600" /> Contact List
      </h1>

      {/* Search bar */}
      <input
        type="text"
        placeholder="Search contact by name..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="p-3 border rounded-xl w-full max-w-2xl mb-8 focus:outline-none focus:ring-2 focus:ring-blue-400"
      />

      {/* Grid of Contact Boxes */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 w-full max-w-7xl">
        {filteredContacts.map((contact, index) => (
          <div
            key={index}
            className={`flex flex-col justify-center items-start p-6 rounded-2xl shadow-lg border border-gray-300 ${colors[index % colors.length]} hover:shadow-2xl transition-all duration-300`}
          >
            <div className="flex items-center gap-2 mb-3">
              <User className="text-blue-600 w-6 h-6" />
              <h2 className="text-lg font-semibold text-gray-800">{contact.name}</h2>
            </div>
            <div className="flex items-center gap-2 text-gray-700 mb-1">
              <Phone className="text-green-600 w-5 h-5" /> {contact.phone}
            </div>
            <div className="flex items-center gap-2 text-gray-700">
              <Mail className="text-red-600 w-5 h-5" /> {contact.email}
            </div>
          </div>
        ))}
      </div>

      {/* Add Contact Button */}
      <button className="mt-10 px-6 py-3 bg-blue-600 text-white rounded-xl font-semibold hover:bg-blue-700 transition">
        Add New Contact
      </button>
    </div>
  );
}
