import React, { useState } from "react";
import { FaUser, FaPhone, FaEnvelope } from "react-icons/fa";
import AddContactForm from "./AddContactForm";

function ContactList({ contacts, onAddContact }) {
  const [showForm, setShowForm] = useState(false);

  return (
    <div>
      {/* Contact cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {contacts.map((contact) => (
          <div
            key={contact.id}
            className="bg-white rounded-xl shadow p-4 flex flex-col items-start border border-gray-200"
          >
            <FaUser className="text-blue-500 text-3xl mb-2" />
            <h2 className="text-lg font-semibold text-gray-800">{contact.name}</h2>
            <p className="text-gray-600 flex items-center mt-1">
              <FaPhone className="mr-2 text-blue-400" /> {contact.phone}
            </p>
            <p className="text-gray-600 flex items-center mt-1">
              <FaEnvelope className="mr-2 text-blue-400" /> {contact.email}
            </p>
          </div>
        ))}
      </div>

      {/* Add Contact button */}
      <div className="text-center mt-8">
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600 transition"
        >
          {showForm ? "Cancel" : "+ Add New Contact"}
        </button>
      </div>

      {/* Show form if toggled */}
      {showForm && (
        <div className="mt-6">
          <AddContactForm onAddContact={(newContact) => {
            onAddContact(newContact);
            setShowForm(false); // Close form after adding
          }} />
        </div>
      )}
    </div>
  );
}

export default ContactList;
