import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { FaPhone, FaEnvelope, FaUser } from "react-icons/fa6";

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    // Example static data (replace with API later)
    const data = [
      { id: 1, name: "Alice Johnson", phone: "9876543210", email: "alice@gmail.com" },
      { id: 2, name: "Bob Smith", phone: "9123456780", email: "bob@gmail.com" },
      { id: 3, name: "Charlie Brown", phone: "9988776655", email: "charlie@gmail.com" },
      { id: 4, name: "David Miller", phone: "9090909090", email: "david@gmail.com" },
    ];
    setContacts(data);
  }, []);

  const filteredContacts = contacts.filter((c) =>
    c.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center py-10">
      <motion.h1
        className="text-4xl font-bold mb-8 flex items-center gap-2 text-blue-600"
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        {/* <FaPhone className="text-blue-500" /> Contact List */}
      </motion.h1>

      {/* <input
        type="text"
        placeholder="ðŸ” Search contact by name..."
        className="w-80 p-3 mb-10 border rounded-xl shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      /> */}

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredContacts.map((contact, index) => (
          <motion.div
            key={contact.id}
            className="bg-white p-6 rounded-2xl shadow-md hover:shadow-xl transition-shadow duration-300"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
          >
            <div className="flex flex-col items-center text-center">
              <FaUser className="text-4xl text-blue-500 mb-3" />
              <h2 className="text-xl font-semibold text-gray-800">{contact.name}</h2>
              <p className="text-gray-600 flex items-center gap-2 mt-2">
                <FaPhone className="text-blue-400" /> {contact.phone}
              </p>
              <p className="text-gray-600 flex items-center gap-2 mt-2">
                <FaEnvelope className="text-blue-400" /> {contact.email}
              </p>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default ContactList;
