import React from "react";
import { Phone, Mail, User } from "lucide-react";

const contacts = [
  {
    name: "Alice Johnson",
    phone: "9876543210",
    email: "alice@gmail.com",
  },
  {
    name: "Bob Smith",
    phone: "9123456780",
    email: "bob@gmail.com",
  },
  {
    name: "Charlie Brown",
    phone: "9988776655",
    email: "charlie@gmail.com",
  },
  {
    name: "David Miller",
    phone: "9000012345",
    email: "david@gmail.com",
  },
];

export default function ContactList() {
  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-2xl font-bold mb-4 flex items-center gap-2">
        <Phone className="text-red-600" /> Contact List
      </h1>

      <input
        type="text"
        placeholder="Search contact by name..."
        className="p-2 border rounded-lg w-full mb-6 focus:outline-none focus:ring-2 focus:ring-blue-400"
      />

      {/* GRID CONTAINER */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {contacts.map((contact, index) => (
          <div
            key={index}
            className="bg-white shadow-lg rounded-2xl p-4 border border-gray-200 hover:shadow-2xl transition-all duration-300"
          >
            <div className="flex items-center gap-2 mb-2">
              <User className="text-blue-500" />
              <h2 className="text-lg font-semibold text-gray-800">
                {contact.name}
              </h2>
            </div>
            <div className="flex items-center gap-2 text-gray-600">
              <Phone className="text-green-500" /> {contact.phone}
            </div>
            <div className="flex items-center gap-2 text-gray-600 mt-1">
              <Mail className="text-red-500" /> {contact.email}
            </div>
          </div>
        ))}
      </div>

      <button className="mt-8 px-4 py-2 bg-blue-600 text-white rounded-xl hover:bg-blue-700">
        Add New Contact
      </button>
    </div>
  );
}
